import React, { useState } from 'react'
import { useNavigate, Link } from 'react-router-dom'

export default function LoginPage () {
  const [email, setEmail] = useState('') // 状态用于存储电子邮件
  const [password, setPassword] = useState('') // 状态用于存储密码
  const navigate = useNavigate()

  const handleLogin = async () => {
    try {
      // 发送 POST 请求到后端登录接口
      const response = await fetch('http://127.0.0.1:8000/users/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          email: email, // 使用用户输入的电子邮件
          password: password // 使用用户输入的密码
        })
      })

      const data = await response.json()
      if (data.message === "Login successful") {
        // 登录成功，根据后端返回的数据进行操作
        navigate('/HomePage') // 导航到首页
      } else {
        // 登录失败，显示错误信息
        alert('登录失败: ' + data.message)
      }
    } catch (error) {
      // 请求出错，显示错误信息
      alert('登录请求失败: ' + error.message)
    }
  }

  return (
    <div className="login-page">
      <div className="login-form">
        <h2>Login</h2>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button onClick={handleLogin}>Login</button>
        <p>
          Don't have an account? <Link to="/register">Register</Link>
        </p>
      </div>
    </div>
  )
}
