import React, { useState, useEffect } from 'react'
import ArticleList from './ArticleList'
import { Link } from 'react-router-dom'
// 假设的弹窗组件
const PreferencesModal = ({ preferences, onSave }) => {
  const [selectedPreferences, setSelectedPreferences] = useState([])

  const handleSave = () => {
    onSave(selectedPreferences)
    // 关闭弹窗逻辑（如果你的弹窗组件不是自动关闭的）
  }

  return (
    <div className="modal">
      <h2>Choose your preferences</h2>
      <ul>
        {preferences.map((preference) => (
          <li key={preference.id}>
            <input
              type="checkbox"
              checked={selectedPreferences.includes(preference.id)}
              onChange={() => {
                setSelectedPreferences((currentPreferences) =>
                  currentPreferences.includes(preference.id)
                    ? currentPreferences.filter((id) => id !== preference.id)
                    : [...currentPreferences, preference.id],
                )
              }}
            />
            {preference.name}
          </li>
        ))}
      </ul>
      <button onClick={handleSave}>Save Preferences</button>
    </div>
  )
}

const HomePage = () => {
  const [isFirstLogin, setIsFirstLogin] = useState(null) // Null表示我们还不知道
  const [preferences, setPreferences] = useState([])

  // 模拟从后端获取用户的首次登录状态和喜好列表
  useEffect(() => {
    // 假设 fetchUserStatus 返回 { isFirstLogin: boolean, preferences: array }
    const fetchUserStatus = async () => {
      const response = await fakeApiCall()
      setIsFirstLogin(response.isFirstLogin)
      setPreferences(response.preferences)
    }

    fetchUserStatus()
  }, [])

  const savePreferences = (userPreferences) => {
    // 保存用户喜好的逻辑
    console.log('Saving user preferences:', userPreferences)
    setIsFirstLogin(false) // 假设保存后用户不再是首次登录
  }

  // 如果还在加载用户信息
  if (isFirstLogin === null) {
    return <div>Loading...</div>
  }

  return (
    <div className="homepage">
      <div className="profile-link">
        <Link to="/profile">Profile Settings</Link>
      </div>
      {isFirstLogin ? (
        <PreferencesModal preferences={preferences} onSave={savePreferences} />
      ) : (
        <ArticleList />
      )}
      {isFirstLogin ? (
        <PreferencesModal preferences={preferences} onSave={savePreferences} />
      ) : (
        <ArticleList />
      )}

    </div>
  )
}

export default HomePage

// 假设的 API 调用函数
const fakeApiCall = () => {
  return Promise.resolve({
    isFirstLogin: true, // 假设用户是第一次登录
    preferences: [
      { id: 1, name: 'Technology' },
      { id: 2, name: 'Science' },
      // 更多喜好
    ],
  })
}
