import React, { useState, useEffect } from 'react'
import ArticleList from './ArticleList'
import { Link } from 'react-router-dom'

const PreferencesModal = ({ preferences, onSave }) => {
  const [selectedPreferences, setSelectedPreferences] = useState([])

  const handleSave = () => {
    onSave(selectedPreferences)
    // 添加关闭弹窗的逻辑
  }

  return (
    <div className="modal">
      <h2>选择您的偏好</h2>
      <ul>
        {preferences.map((preference) => (
          <li key={preference.id}>
            <input
              type="checkbox"
              checked={selectedPreferences.includes(preference.id)}
              onChange={() => {
                setSelectedPreferences((currentPreferences) =>
                  currentPreferences.includes(preference.id)
                    ? currentPreferences.filter((id) => id !== preference.id)
                    : [...currentPreferences, preference.id]
                )
              }}
            />
            {preference.name}
          </li>
        ))}
      </ul>
      <button onClick={handleSave}>保存偏好</button>
    </div>
  )
}

const HomePage = () => {
  const [isFirstLogin, setIsFirstLogin] = useState(null) // null表示我们还不知道用户是否是第一次登录
  const [preferences, setPreferences] = useState([])

  useEffect(() => {
    const fetchUserStatus = async () => {
      const response = await fakeApiCall() // 假设的API调用
      setIsFirstLogin(response.isFirstLogin)
      setPreferences(response.preferences)
    }

    fetchUserStatus()
  }, [])

  const savePreferences = (userPreferences) => {
    console.log('保存用户偏好:', userPreferences)
    setIsFirstLogin(false) // 保存偏好后更新首次登录状态
  }

  if (isFirstLogin === null) {
    return <div>加载中...</div>
  }

  return (
    <div className="homepage">
      <div className="profile-link">
        <Link to="/profile">个人设置</Link>
      </div>
      {isFirstLogin ? (
        <PreferencesModal preferences={preferences} onSave={savePreferences} />
      ) : (
        <ArticleList />
      )}
    </div>
  )
}

export default HomePage

const fakeApiCall = () => {
  // 这个函数模拟了一个API调用，返回用户是否是第一次登录以及偏好设置
  return Promise.resolve({
    isFirstLogin: true, // 假设用户是第一次登录
    preferences: [
      { id: 1, name: '技术' },
      { id: 2, name: '科学' },
      // 可能还有更多的偏好设置
    ],
  })
}
